import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        String s = in.nextLine();
        if (s.equals("0")) {
            return;
        }
        List<Shape> shapeList = new ArrayList<>();
        while (!s.equals("0")) {
            int type = Integer.parseInt(s.split(" ")[0]);
            switch (type) {
                case 1:
                    shapeList.add(new Circle(Double.parseDouble(s.split(" ")[1])));
                    break;
                case 2:
                    shapeList.add(new Rectangle(Double.parseDouble(s.split(" ")[1]), Double.parseDouble(s.split(" ")[2])));
                    break;
                case 3:
                    shapeList.add(new Triangle(Double.parseDouble(s.split(" ")[1]), Double.parseDouble(s.split(" ")[2]), Double.parseDouble(s.split(" ")[3])));
                    break;
                case 4:
                    shapeList.add(new Trapezoid(Double.parseDouble(s.split(" ")[1]), Double.parseDouble(s.split(" ")[2]), Double.parseDouble(s.split(" ")[3])));
                    break;
                default:
                    System.out.println("Wrong Format");
                    return;
            }
            s = in.nextLine();
        }
        System.out.println("The original list:");
        for (Shape shape : shapeList) {
            System.out.print(shape.toString());
        }
        Collections.sort(shapeList);
        System.out.println();
        System.out.println("The sorted list:");
        for (Shape shape : shapeList) {
            System.out.print(shape.toString());
        }
        System.out.println();
        System.out.println("Sum of area:" + getArea(shapeList));
